jQuery(document).ready(function($) {
  var referQuery = 'refer';
  var queryUrl = window.location.search;
  if( queryUrl.indexOf(referQuery) > -1 ){
    var regex = /refer=([^&]+)&?/gm;
    var m = regex.exec(queryUrl);
    if( m && Array.isArray(m) && m[1] != undefined ){
      var refStr = '?refer='+m[1];
      updateLinksRefer('a[href*="dashboard"]', refStr);
      updateLinksRefer('.language-block a[href$=".html"]', refStr );
    }
  }
});

function updateLinksRefer(selector, refStr){
  $(selector).each(function(index, el) {
     var old = $(this).prop('href');
     $(this).prop('href', old+refStr);
  });
}

