function initGlobe() {
    function whichTransitionEvent() {
        var t,
            el = document.createElement("fakeelement");

        var transitions = {
            "transition": "transitionend",
            "OTransition": "oTransitionEnd",
            "MozTransition": "transitionend",
            "WebkitTransition": "webkitTransitionEnd"
        };

        for (t in transitions) {
            if (el.style[t] !== undefined) {
                return transitions[t];
            }
        }
    }

    var transitionEvent = whichTransitionEvent();

    var randomX;
    var randomY;
    var marker;
    var markerArray = [];

    var docHeight = $(document).height(),
        docWidth = $(document).width(),
        $div = $('.visual-globe .area-big'),
        divWidth = $div.width(),
        divHeight = $div.height(),
        heightMax = divHeight,
        widthMax = divWidth;

        $divsmall = $('.visual-globe .area-small'),
        divWidthSmall = $divsmall.width(),
        divHeightSmall = $divsmall.height(),
        heightMaxSmall = divHeightSmall,
        widthMaxSmall = divHeightSmall;

        function createMarker() {
            marker = $("<div class='marker'>");
            $(".visual-globe .area-big").append(marker);

            setStartpnt();
        }

        function createMarkerSmall() {
            marker = $("<div class='marker'>");
            $(".visual-globe .area-small").append(marker);

            setStartpntSmall();
        }

        function setStartpnt() {
            randomX = Math.floor(widthMax * Math.random());
            randomY = Math.floor(heightMax * Math.random());
            marker.css({
                "left": + randomX + "px",
                "top": + randomY + "px"
            });
        }

        function setStartpntSmall() {
            randomX = Math.floor(widthMaxSmall * Math.random());
            randomY = Math.floor(heightMaxSmall * Math.random());
            marker.css({
                "left": + randomX + "px",
                "top": + randomY + "px"
            });
        }

    for (i = 0; i < 9; i++) {
        createMarker();
    }

    for (i = 0; i < 3; i++) {
        createMarkerSmall();
    }

    $('.marker').each(function(key, val) {
        setTimeout(function() {
            fadeIn(this);
        }.bind(this), getRandomInt(10, 2000));
    });


    function fadeIn(elem) {
        $(elem).css({
            'opacity': 1
        });
    }

    function getRandomInt(min, max) {
        return Math.floor(Math.random() * (max - min + 1)) + min;
    }

}

function progressBar(percent, $element) {
    var progressBarWidth = percent * $element.width() / 100;
    $element.find('div').animate({ width: progressBarWidth }, 1000);
}

